mapper.select:
SELECT id,
       url,
       description,
       type,
       permission_expression,
       method,
       sort,
       parent_id,
       deleted,
       created_time,
       modified_time
FROM permission


        AND id = #{id}


        AND url = #{url}


        AND description = #{description}


        AND type = #{type}


        AND permission_expression = #{permissionExpression}


        AND method = #{method}


        AND sort = #{sort}


        AND parent_id = #{parentId}


        AND deleted = #{deleted}


        AND created_time = #{createdTime}


        AND modified_time = #{modifiedTime}
